import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class ToDoInput extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
        <form id="inputForm" onSubmit={this.props.handleSubmit}>
          <label>
            To Do:
            <input type="text" value={this.props.value} onChange=
          {this.props.handleChange} />
          </label>
            <input type="submit" value="Submit" />
        </form>
    );
  }
}

class ToDo extends Component {
  render() {
    return (
      <li>
        {this.props.item}
      </li>
    );
  }
}

class ToDoList extends Component {

  render() {
    const listItems = this.props.items.map(function(item, i) {
      return (
        <ToDo
          key={i}
          item={item}/>
      )

    });
    return (
      <ul>
        {listItems}
      </ul>
    );
  }
}


class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        arr: [],
        value: ''
      }
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      this.setState({
        arr: this.state.arr.concat(this.state.value),
        value: ''
      });
      event.preventDefault();
    }

  render() {
    return (
      <div className="App">
        <ToDoInput
          handleChange = {this.handleChange}
          handleSubmit = {this.handleSubmit}
          value = {this.state.value}/>
        <ToDoList items = {this.state.arr}>
        </ToDoList>
      </div>
    );
  }
}

export default App;
